// 记录所有请求数据用于调试
const body = $input.item.json.body;
const headers = $input.item.json.headers || {};

console.log('=== QQ Bot Debug Info ===');
console.log('Full body:', JSON.stringify(body, null, 2));
console.log('OpCode:', body.op);
console.log('Event type:', body.t);
console.log('Headers:', JSON.stringify(headers, null, 2));

// 处理回调验证 (OpCode 13)
if (body.op === 13) {
  const plainToken = body.d?.plain_token;
  const eventTs = body.d?.event_ts;

  console.log('Validation request detected');
  console.log('plain_token:', plainToken);
  console.log('event_ts:', eventTs);

  // 返回 QQ 要求的格式
  return {
    json: {
      plain_token: plainToken,
      signature: 'temp-signature-' + eventTs
    }
  };
}

// 其他事件
return {
  json: {
    message: 'Event received',
    op: body.op,
    type: body.t,
    data: body.d
  }
};
