// ‰ΩøÁî® tweetnacl Â∫ì

const nacl = require('tweetnacl');
const util = require('tweetnacl-util');

const QQ_BOT_SECRET = 'SYfmu2AJScmx8KWjw9Nbq5Lbs9Rj1Kdx';

const body = $input.item.json.body;

console.log('Received op:', body.op);

// ÂõûË∞ÉÂú∞ÂùÄÈ™åËØÅ (OpCode 13)
if (body.op === 13) {
  const plainToken = body.d.plain_token;
  const eventTs = body.d.event_ts;

  console.log('Validation request:');
  console.log('  plain_token:', plainToken);
  console.log('  event_ts:', eventTs);

  // ÁîüÊàê Ed25519 ÂØÜÈí•ÂØπ
  // Ê†πÊçÆ Bot Secret ÁîüÊàê 32 Â≠óËäÇ seed
  let seed = QQ_BOT_SECRET;
  while (seed.length < 32) {
    seed += seed;
  }
  seed = seed.substring(0, 32);

  // Â∞ÜÂ≠óÁ¨¶‰∏≤ËΩ¨Êç¢‰∏∫ Uint8Array
  const seedBytes = util.decodeUTF8(seed);

  // ÁîüÊàêÂØÜÈí•ÂØπ
  const keyPair = nacl.sign.keyPair.fromSeed(seedBytes);

  // Á≠æÂêçÂÜÖÂÆπ: event_ts + plain_token
  const message = eventTs + plainToken;
  const messageBytes = util.decodeUTF8(message);

  // ÁîüÊàêÁ≠æÂêç
  const signatureBytes = nacl.sign.detached(messageBytes, keyPair.secretKey);

  // ËΩ¨Êç¢‰∏∫ÂçÅÂÖ≠ËøõÂà∂Â≠óÁ¨¶‰∏≤
  const signature = util.encodeBase64(signatureBytes);

  console.log('Generated signature:', signature);

  // ËøîÂõû QQ Ë¶ÅÊ±ÇÁöÑÊ†ºÂºè
  return {
    json: {
      plain_token: plainToken,
      signature: signature
    }
  };
}

// Â∏∏ËßÑ‰∫ã‰ª∂Â§ÑÁêÜ
return {
  json: {
    message: 'Event received',
    op: body.op,
