// QQ Bot Ed25519 Signature Service
// ÈÉ®ÁΩ≤Âà∞ Railway, Render ÊàñÂÖ∂‰ªñ Node.js ÊâòÁÆ°ÊúçÂä°

const express = require('express');
const crypto = require('crypto');

const app = express();
const PORT = process.env.PORT || 3000;

// QQ Êú∫Âô®‰∫∫ÈÖçÁΩÆÔºà‰ªéÁéØÂ¢ÉÂèòÈáèËØªÂèñÔºâ
const QQ_BOT_SECRET = process.env.QQ_BOT_SECRET || 'SYfmu2AJScmx8KWjw9Nbq5Lbs9Rj1Kdx';

app.use(express.json());

// ÂÅ•Â∫∑Ê£ÄÊü•
app.get('/health', (req, res) => {
  res.json({ status: 'ok', service: 'QQ Bot Signature Service' });
});

// QQ Êú∫Âô®‰∫∫ÂõûË∞ÉÈ™åËØÅÁ´ØÁÇπ
app.post('/qq-bot-callback', (req, res) => {
  console.log('Received request:', JSON.stringify(req.body, null, 2));

  const body = req.body;

  // Â§ÑÁêÜÂõûË∞ÉÂú∞ÂùÄÈ™åËØÅ (OpCode 13)
  if (body.op === 13) {
    const plainToken = body.d.plain_token;
    const eventTs = body.d.event_ts;

    console.log('Validation request:');
    console.log('  plain_token:', plainToken);
    console.log('  event_ts:', eventTs);

    // ÁîüÊàê Ed25519 Á≠æÂêç
    // Ê≥®ÊÑè: ËøôÈáå‰ΩøÁî® sodium Â∫ì
    const sodium = require('sodium-native');

    // ÁîüÊàê 32 Â≠óËäÇ seed
    let seed = QQ_BOT_SECRET;
    while (seed.length < 32) {
      seed += seed;
    }
    seed = seed.substring(0, 32);

    const seedBuffer = Buffer.from(seed, 'utf8');
    const seedBuffer32 = Buffer.alloc(32);
    seedBuffer.copy(seedBuffer32);

    // ÁîüÊàê Ed25519 ÂØÜÈí•ÂØπ
    const keyPair = sodium.crypto_sign_seed_keypair(seedBuffer32);

    // Á≠æÂêçÂÜÖÂÆπ: event_ts + plain_token
    const message = Buffer.from(eventTs + plainToken);
    const signature = Buffer.alloc(sodium.crypto_sign_BYTES);
    sodium.crypto_sign_detached(signature, message, keyPair.secretKey);

    // ËΩ¨Êç¢‰∏∫ÂçÅÂÖ≠ËøõÂà∂
    const signatureHex = signature.toString('hex');

    console.log('Generated signature:', signatureHex);

    // ËøîÂõû QQ Ë¶ÅÊ±ÇÁöÑÊ†ºÂºè
    return res.json({
      plain_token: plainToken,
      signature: signatureHex
    });
  }

  // Â∏∏ËßÑ‰∫ã‰ª∂Â§ÑÁêÜ
  console.log('Event received, op:', body.op, 'type:', body.t);

  // ËΩ¨ÂèëÂà∞ n8n
  // TODO: Ê∑ªÂä† n8n webhook URL
  res.json({
    message: 'Event received',
    op: body.op,
    type: body.t
  });
});

app.listen(PORT, () => {
  console.log(`QQ Bot Signature Service running on port ${PORT}`);
  console.log(`Webhook URL: https://your-domain.railway.app/qq-bot-callback`);
});

