     1‚Üí// QQ Bot Ed25519 Signature Service
     2‚Üí// ÈÉ®ÁΩ≤Âà∞ Railway, Render ÊàñÂÖ∂‰ªñ Node.js ÊâòÁÆ°ÊúçÂä°
     3‚Üí
     4‚Üíconst express = require('express');
     5‚Üíconst crypto = require('crypto');
     6‚Üí
     7‚Üíconst app = express();
     8‚Üíconst PORT = process.env.PORT || 3000;
     9‚Üí
    10‚Üí// QQ Êú∫Âô®‰∫∫ÈÖçÁΩÆÔºà‰ªéÁéØÂ¢ÉÂèòÈáèËØªÂèñÔºâ
    11‚Üíconst QQ_BOT_SECRET = process.env.QQ_BOT_SECRET || 'SYfmu2AJScmx8KWjw9Nbq5Lbs9Rj1Kdx';
    12‚Üí
    13‚Üíapp.use(express.json());
    14‚Üí
    15‚Üí// ÂÅ•Â∫∑Ê£ÄÊü•
    16‚Üíapp.get('/health', (req, res) => {
    17‚Üí  res.json({ status: 'ok', service: 'QQ Bot Signature Service' });
    18‚Üí});
    19‚Üí
    20‚Üí// QQ Êú∫Âô®‰∫∫ÂõûË∞ÉÈ™åËØÅÁ´ØÁÇπ
    21‚Üíapp.post('/qq-bot-callback', (req, res) => {
    22‚Üí  console.log('=== QQ Bot Callback Request ===');
    23‚Üí  console.log('Headers:', JSON.stringify(req.headers, null, 2));
    24‚Üí  console.log('Body:', JSON.stringify(req.body, null, 2));
    25‚Üí
    26‚Üí  const body = req.body;
    27‚Üí
    28‚Üí  // Â§ÑÁêÜÂõûË∞ÉÂú∞ÂùÄÈ™åËØÅ (OpCode 13)
    29‚Üí  if (body.op === 13) {
    30‚Üí    const plainToken = body.d.plain_token;
    31‚Üí    const eventTs = body.d.event_ts;
    32‚Üí
    33‚Üí    console.log('Validation request detected:');
    34‚Üí    console.log('  plain_token:', plainToken);
    35‚Üí    console.log('  event_ts:', eventTs);
    36‚Üí    console.log('  Bot Secret:', QQ_BOT_SECRET);
    37‚Üí
    38‚Üí    // ÁîüÊàê Ed25519 Á≠æÂêç
    39‚Üí    const sodium = require('sodium-native');
    40‚Üí
    41‚Üí    // ÁîüÊàê 32 Â≠óËäÇ seed - ÊåâÁÖßÊñáÊ°£ repeat Áõ¥Âà∞ËææÂà∞ 32 Â≠óËäÇ
    42‚Üí    let seed = QQ_BOT_SECRET;
    43‚Üí    while (seed.length < 32) {
    44‚Üí      seed = seed + seed;
    45‚Üí    }
    46‚Üí    seed = seed.substring(0, 32);
    47‚Üí
    48‚Üí    console.log('  Generated seed:', seed);
    49‚Üí    console.log('  Seed length:', seed.length);
    50‚Üí
    51‚Üí    const seedBuffer = Buffer.from(seed, 'utf8');
    52‚Üí
    53‚Üí    // ÁîüÊàê Ed25519 ÂØÜÈí•ÂØπ
    54‚Üí    const publicKey = Buffer.alloc(sodium.crypto_sign_PUBLICKEYBYTES);
    55‚Üí    const secretKey = Buffer.alloc(sodium.crypto_sign_SECRETKEYBYTES);
    56‚Üí    sodium.crypto_sign_seed_keypair(publicKey, secretKey, seedBuffer);
    57‚Üí
    58‚Üí    console.log('  Public key:', publicKey.toString('hex'));
    59‚Üí    console.log('  Secret key:', secretKey.toString('hex').substring(0, 32) + '...');
    60‚Üí
    61‚Üí    // Á≠æÂêçÂÜÖÂÆπ: event_ts + plain_token
    62‚Üí    const message = Buffer.from(eventTs + plainToken);
    63‚Üí    console.log('  Message to sign:', eventTs + plainToken);
    64‚Üí
    65‚Üí    const signature = Buffer.alloc(sodium.crypto_sign_BYTES);
    66‚Üí    sodium.crypto_sign_detached(signature, message, secretKey);
    67‚Üí
    68‚Üí    // ËΩ¨Êç¢‰∏∫ÂçÅÂÖ≠ËøõÂà∂
    69‚Üí    const signatureHex = signature.toString('hex');
    70‚Üí
    71‚Üí    console.log('  Generated signature:', signatureHex);
    72‚Üí    console.log('  Signature length:', signatureHex.length);
    73‚Üí
    74‚Üí    // ËøîÂõû QQ Ë¶ÅÊ±ÇÁöÑÊ†ºÂºè
    75‚Üí    const response = {
    76‚Üí      plain_token: plainToken,
    77‚Üí      signature: signatureHex
    78‚Üí    };
    79‚Üí
    80‚Üí    console.log('=== Sending response ===');
    81‚Üí    console.log('Response:', JSON.stringify(response, null, 2));
    82‚Üí
    83‚Üí    return res.json(response);
    84‚Üí  }
    85‚Üí
    86‚Üí  // Â∏∏ËßÑ‰∫ã‰ª∂Â§ÑÁêÜ
    87‚Üí  console.log('Event received, op:', body.op, 'type:', body.t);
    88‚Üí
    89‚Üí  // ËΩ¨ÂèëÂà∞ n8n
    90‚Üí  // TODO: Ê∑ªÂä† n8n webhook URL
    91‚Üí  res.json({
    92‚Üí    message: 'Event received',
    93‚Üí    op: body.op,
    94‚Üí    type: body.t
    95‚Üí  });
    96‚Üí});
    97‚Üí
    98‚Üíapp.listen(PORT, () => {
    99‚Üí  console.log(`QQ Bot Signature Service running on port ${PORT}`);
   100‚Üí  console.log(`Webhook URL: https://your-domain.railway.app/qq-bot-callback`);
   101‚Üí});
   102‚Üí

